mapper.select:
select
userid, shopid, userComment, score, create_time, update_time, reply
from comments


        and userid = #{userid}


        and shopid = #{shopid}


        and userComment = #{usercomment}


        and score = #{score}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and reply = #{reply}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from comments


        and userid = #{userid}


        and shopid = #{shopid}


        and userComment = #{usercomment}


        and score = #{score}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and reply = #{reply}
