mapper.select:
select
id, name, typed_id, address, sold, open_hours, create_time, update_time, introduction, img
from shop


        and id = #{id}


        and name = #{name}


        and typed_id = #{typedId}


        and address = #{address}


        and sold = #{sold}


        and open_hours = #{openHours}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and introduction = #{ introduction}


        and img = #{img}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from shop


        and id = #{id}


        and name = #{name}


        and typed_id = #{typedId}


        and address = #{address}


        and sold = #{sold}


        and open_hours = #{openHours}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and introduction = #{ introduction}


        and img = #{img}
