mapper.select:
select
id, username, password, introduction, userType, shopid
from user


        and id = #{id}


        and username = #{username}


        and password = #{password}


        and introduction = #{ introduction}


        and userType = #{usertype}


        and shopid = #{shopid}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from user


        and id = #{id}


        and username = #{username}


        and password = #{password}


        and introduction = #{ introduction}


        and userType = #{usertype}


        and shopid = #{shopid}
